import path from "path";

var errorHandler = function errorHandler(err, _req, res, _next) {
  return res.status(404).json({
    status: "error",
    message: err.message,
    stack: // print a nicer stack trace by splitting line breaks and making them array items
    process.env.NODE_ENV === "development" && (err.stack || "").split("\n").map(function (line) {
      return line.trim();
    }).map(function (line) {
      return line.split(path.sep).join("/");
    }).map(function (line) {
      return line.replace(process.cwd().split(path.sep).join("/"), ".");
